import ReactTooltip from "react-tooltip";
import { BioDetails } from "../BioDetails/BioDetails";
import { BIO } from "../../constants/bio";
import Tooltip from '@mui/material/Tooltip';

import "./Bio.css";

export const Bio = () => {
  const [randomID, setRandomID] = useState(String(Math.random()))

  return (
    <section className="container container-lines">
      <p className="purple-text">
        <span className="purple-smoke-text">Experience</span>{" "}
      </p>
      <div className="line"></div>
      {BIO.map((e) => (
        <div key={e.year} className="lines-items">
          <span className="white-text">{e.year}: </span>
          <span className="gray-text" data-tip="soo cuteeeee">
            {e.title.cargo} at {e.title.name}
            <BioDetails name={e.title.name} description={e.description} />
            <ReactTooltip getContent={(dataTip) => `This little buddy is ${dataTip}`}
                place="top"
                effects="float"
                className="custom-theme"
            >

            <BioDetails name={e.title.name} description={e.description} />          
            </ReactTooltip>
          </span>
          <div className="line"></div>
        </div>
      ))}
    </section>
  );
};
