export const BIO = [
  {
    year: 2020,
    title: {
      name: "CUBIT @ Stony Brook University",
      cargo: "Student Researcher",
    },
    description:
      "Development of ads, covers, and social media using design methodologies such as Design Thinking",
    details: ["Adobe Photoshop", "Adobe Illustrator", "Adobe InDesign"],
  },
  {
    year: 2020,
    title: {
      name: "Dive Chat",
      cargo: "Product Intern",
    },
    description:
      "Development of ads, covers, and social media using design methodologies such as Design Thinking",
    details: ["Adobe Photoshop", "Adobe Illustrator", "Adobe InDesign"],
  },
  {
    year: 2021,
    title: {
      name: "Stony Brook University",
      cargo: "Resident Assistant",
    },
    description: "Resident Assistant directly overseeing fourty upperclassmen residents, on-call for over four hundred.",
    details: [
      "Assigned rotation based, overnight emergency response for over five hundred residents and up to two apartment-style residential buildings, including building and area wide emergency response in situations such as building flooding, electrical outages, fire alarms, mental health issues, etc.",
      "Development of user interface and user experience",
    ],
  },
  {
    year: 2022,
    title: {
      name: "Stony Brook University",
      cargo: "Teaching Assistant",
    },
    description:
      "UGTA for BIO 201 (Ecology & Evolution), CSE 215 (Intro to Theory of Computation)",
    details: ["Evauluated student assignmenets", "Led recitations and prepared recitation materials for more than 20 students", "Held weekly office hours", "Reported student and instructor feedback to university initiatives regarding diversity and equity"],
  },
];
